package com.leetcode;

import org.junit.Before;
import org.junit.Test;

/**
 * Created by sunilpatil on 12/4/16.
 */
public class Problem389Test {
    Problem389 problem;

    @Before
    public void setup() {
        problem = new Problem389();
    }

    @Test
    public void testLastChar() {
        String first = "abcd";
        String second = "abcde";
        assert problem.findTheDifference(first, second) == 'e';
    }

    @Test
    public void testMiddleChar() {
        String first = "abde";
        String second = "abcde";
        assert problem.findTheDifference(first, second) == 'c';
    }

    public void testLongString() {
        String first = "";
        String second = "";
        assert problem.findTheDifference(first, second) == 't';

    }
}
